mapper.select:
		select `id`,`title`,`large_areas`,`small_areas`,`square_meter`,`orientation`,`house_type`,`pubdate`,`price`,`description`,`address`,`add_time`,`landlord_id`,`tenant_id`,`elevator`,`tv`,`fridge`,`air_conditioner`,`broad_band`,`wardrobe`,`createtime`,`updatetime`,`status` from house

		  		   and id = #{id} 
		  		   and title =#{title} 
		  		   and large_areas = #{largeAreas} 
			       and status = #{status} 
		  		   and small_areas = #{smallAreas} 
		  		   and square_meter = #{squareMeter} 
		  		   and orientation = #{orientation} 
		  		   and house_type = #{houseType} 
		  		   and pubdate = #{pubdate} 
		  		   and price = #{price} 
		  		   and description = #{description} 
		  		   and address = #{address} 
		  		   and add_time = #{addTime} 
		  		   and landlord_id = #{landlordId} 
		  		   and tenant_id = #{tenantId} 
		  		   and elevator = #{elevator} 
		  		   and tv = #{tv} 
		  		   and fridge = #{fridge} 
		  		   and air_conditioner = #{airConditioner} 
		  		   and broad_band = #{broadBand} 
		  		   and wardrobe = #{wardrobe} 
		  		   and createtime = #{createtime} 
		  		   and updatetime = #{updatetime} 



                order by ${sort} ${asc}


                order by id asc



			limit ${offset}, ${limit}

	select `id`,`title`,`large_areas`,`small_areas`,`square_meter`,`orientation`,`house_type`,`pubdate`,`price`,`description`,`address`,`add_time`,`landlord_id`,`tenant_id`,`elevator`,`tv`,`fridge`,`air_conditioner`,`broad_band`,`wardrobe`,`createtime`,`updatetime`,`status` from house

       and large_areas=#{largeAreas} 
       and orientation=#{orientation} 
       and status=#{status} 
       and house_type like '${houseType}ход%'  
       and price between #{priceStart} and #{priceEnd}

select count(*) from house

	 and large_areas=#{largeAreas} 
