mapper.select:
		select `id`,`parent_id`,`name`,`url`,`permission`,`type`,`img`,`sort`,`createtime`,`updatetime` from permission

		  		   and id = #{id} 
		  		   and parent_id = #{parentId} 
		  		   and name = #{name} 
		  		   and url = #{url} 
		  		   and sort = #{sort} 
		  		   and permission = #{permission} 
		  		   and type = #{type} 
		  		   and img = #{img} 
		  		   and createtime = #{createtime} 
		  		   and updatetime = #{updatetime} 



                order by ${sortClu} ${order}


                order by id



			limit #{offset}, #{limit}

select count(*) from permission

  		   and id = #{id} 
  		   and parent_id = #{parentId} 
  		   and name = #{name} 
	       and url = #{url} 
	       and permission = #{permission} 
  		   and type = #{type} 
  		   and img = #{img} 
	 	   and sort = #{sort} 
	       and createtime = #{createtime} 
  		   and updatetime = #{updatetime}

mapper.update:
update permission
`parent_id` = #{parentId}, `name` = #{name}, url = #{url}, `permission` = #{permission}, `type` = #{type}, sort = #{sort} `img` = #{img}, `createtime` = #{createtime}, `updatetime` = #{updatetime}
where id = #{id}
